import React, { useEffect, useState } from "react";
import axios from "axios";

function TableFournisseur() {
  const [fournisseurs, setFournisseurs] = useState([]);

  useEffect(() => {
    const fetchFournisseurs = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/v1/getAllFournisseur");
        setFournisseurs(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFournisseurs();
  }, []);

  return (
    <div>
      <div className="flex justify-center m-10 text-3xl text-base-300 font-bold justify-between">
        <h1>Table Fournisseur</h1>
        <button className="btn" onClick={()=>window.my_modal_1.showModal()}>Add Fournisseur</button>
        <dialog id="my_modal_1" className="modal">
        <form method="dialog" className="modal-box flex flex-col items-center gap-10">
          <h3 className="font-bold text-lg">Add Fournisseur</h3>
          <input type="text" placeholder="Name" className="input input-bordered w-full max-w-xs" />
          <input type="text" placeholder="Contact" className="input input-bordered w-full max-w-xs" />
          <div className="modal-action flex">
            {/* if there is a button in form, it will close the modal */}
            <button className="btn">Add</button>
          </div>
        </form>
      </dialog>
      </div>
      <div className="scrolling-touch m-10 border border-base-300">
        <div className="overflow-x-auto">
          <table className="table">
            <thead>
              <tr className="text-base-300 text-lg">
                <th></th>
                <th>name</th>
                <th>contact</th>
                <th>edit</th>
              </tr>
            </thead>
            <tbody>
              {fournisseurs.map((fournisseur) => (
                <tr key={fournisseur.id}>
                  <td></td>
                  <td>
                    <div className="flex items-center space-x-3">
                      <div>
                        <div className="font-bold">{fournisseur.name}</div>
                      </div>
                    </div>
                  </td>
                  <td>{fournisseur.contact}</td>
                  <td className="flex gap-2">
                    <button className="btn btn-ms">Edit</button>
                    <button className="btn btn-ms btn-ghost btn-outline">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TableFournisseur;
