import React, { useEffect, useState } from 'react';
import axios from 'axios';

function TableRessource() {
    const [ressource, setRessources] = useState([]);
    const [currentClientName, setCurrentClientName] = useState("");
    const [currentPhone, setCurrentPhone] = useState("");
    const [currentEmail, setCurrentEmail] = useState("");
    const [editId, setEditId] = useState(null);
    const [editIndex, setEditIndex] = useState(null);
    const [counter, setCounter] = useState(0);

    useEffect(() => {
      const fetchRessource = async () => {
        try {
          const response = await axios.get(
            "http://localhost:8080/api/v1/getAllRessource"
          );
          setRessources(response.data);
        } catch (error) {
          console.error(error);
        }
      };

      fetchRessource();
    }, []);

    const fetchRessourceDetails = async (id) => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/v1/getRessourceById/${id}`
        );
        const ressourceDetails = response.data;
        setCurrentClientName(ressourceDetails.clientname);
        setCurrentPhone(ressourceDetails.phone);
        setCurrentEmail(ressourceDetails.email);
      } catch (error) {
        console.error(error);
      }
    };

    const handleEditClick = (id, index) => {
      setEditId(id);
      setEditIndex(index);
      fetchRessourceDetails(id);
      window.my_modal_2.showModal();
    };

    const handleEditSubmit = async (event) => {
      event.preventDefault();

      try {
        const updatedRessource = {
          clientname: currentClientName,
          phone: currentPhone,
          email: currentEmail,
        };

        await axios.put(
          `http://localhost:8080/api/v1/updateRessource/${editId}`,
          updatedRessource
        );

        // Update the fournisseur in the local state
        const updatedRessources = [...ressource];
        updatedRessources[editIndex] = {
          ...updatedRessources[editIndex],
          clientname: currentClientName,
          phone: currentPhone,
          email: currentEmail,
        };
        setRessources(updatedRessources);

        // Clear the form and close the modal
        setCurrentClientName("");
        setCurrentPhone("");
        setCurrentEmail("");
        window.my_modal_2.close();
      } catch (error) {
        console.error(error);
      }
    };

    const handleDeleteClick = async (id) => {
      try {
        await axios.delete(
          `http://localhost:8080/api/v1/deleteRessourceById/${id}`
        );

        // Remove the fournisseur from the local state
        const updatedRessources = materiel.filter(
          (materiels) => materiels.id !== id
        );
        setMateriels(updatedMateriels);
      } catch (error) {
        console.error(error);
      }
    };

    const handleAddSubmit = async (event) => {
      event.preventDefault();

      try {
        const newMateriel = {
          id: counter + 1,
          name: currentName,
          quantite: currentQuantite,
        };

        const response = await axios.post(
          "http://localhost:8080/api/v1/addRessource",
          newMateriel
        );

        const createdMateriel = response.data;

        // Add the new fournisseur to the local state
        setMateriels([...materiel, createdMateriel]);

        // Clear the form and close the modal
        setCounter(counter + 1);
        setCurrentName("");
        setCurrentQuantite("");
        window.my_modal_3.close();
      } catch (error) {
        console.error(error);
      }
    };

  return (
    <div>
      <div className="flex justify-center m-10 text-3xl text-base-300 font-bold justify-between">
        <h1>Table Ressource</h1>
        <button className="btn btn-ms" onClick={() => window.my_modal_1.showModal()}>Add Ressource</button>
        <dialog id="my_modal_1" className="modal">
          <form
            method="dialog"
            className="modal-box flex flex-col items-center gap-10"
            // onSubmit={handleAddSubmit}
          >
            <h3 className="font-bold text-lg">Add Fournisseur</h3>
            <input
              type="text"
              placeholder="Name"
              className="input input-bordered w-full max-w-xs"
              // value={currentName}
              // onChange={(e) => setCurrentName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Contact"
              className="input input-bordered w-full max-w-xs"
              // value={currentContact}
              // onChange={(e) => setCurrentContact(e.target.value)}
            />
            <div className="modal-action flex">
              <button type="submit" className="btn">
                Add
              </button>
            </div>
          </form>
        </dialog>
      </div>
      <div className="scrolling-touch m-10 border border-base-300 ">
        <div className="overflow-x-auto">
          <table className="table">
            {/* head */}
            <thead>
              <tr className="text-base-300 text-lg">
                <th></th>
                <th>client name</th>
                <th>email</th>
                <th>phone number</th>
                <th>edit</th>
              </tr>
            </thead>
            <tbody>
              {/* rows */}
              {ressource.map((ressource) => (
                <tr key={ressource.id}>
                  <th></th>
                  <td>
                    <div className="flex items-center">{ressource.clientname}</div>
                  </td>
                  <td>
                    <div className="font-bold">{ressource.email}</div>
                  </td>
                  <td>
                    <div>{ressource.phone}</div>
                  </td>
                  <td className="flex gap-2">
                  <dialog id="my_modal_2" className="modal">
                      <form
                        method="dialog"
                        className="modal-box flex flex-col items-center gap-10"
                        // onSubmit={handleEditSubmit}
                      >
                        <h3 className="font-bold text-lg">Edit Fournisseur</h3>
                        <input
                          type="text"
                          // defaultValue={currentName}
                          className="input input-bordered w-full max-w-xs"
                          // onChange={(e) => setCurrentName(e.target.value)}
                        />
                        <input
                          type="text"
                          // defaultValue={currentContact}
                          className="input input-bordered w-full max-w-xs"
                          // onChange={(e) => setCurrentContact(e.target.value)}
                        />
                        <div className="modal-action flex">
                          <button type="submit" className="btn">
                            Submit
                          </button>
                        </div>
                      </form>
                    </dialog>
                    <button className="btn btn-ms"onClick={() => window.my_modal_2.showModal()}>Edit</button>
                    <button className="btn btn-ms btn-ghost btn-outline">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TableRessource;
