import React, { useState } from 'react'
import Layout from '../../components/Layout/Layout'
import axios from 'axios';

const Addreclamation = () => {
  const [titre, setTitre] = useState('');
  const [contenu, setContenu] = useState('');
  
  const addReclamation = async (e) => {

    try {
      const userID = Math.floor(Math.random() * 3);
      
      const result = await axios.post(`http://localhost:8080/api/reclamation/add/${userID}`,{
        titre: titre,
        contenu: contenu
      });
      if(result.status===200){
        setTitre("")
        setContenu("")

      }
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <div className='w-full' >
         <Layout role={1} />
        <div className='w-80 flex justify-center items-center  '>
        <form className='w-full' onSubmit={addReclamation}>
        <div class="mb-6">
            <label for="titre" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Titre</label>
            <input onChange={(e)=>setTitre(e.target.value)}  type="text" id="titre" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Entrer le titre de reclamation" required/>
        </div>
        <div class="mb-6">
        <label for="message" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Contenu</label>      
        <textarea  onChange={(e)=>setContenu(e.target.value)} id="message" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Ecrire le contenu de votre reclamation..."></textarea>

        </div>
       
        <button type="submit" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-auto px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Submit</button>
        </form>
        </div>
        

    </div>
  )
}

export default Addreclamation
