import React, { useEffect, useState } from "react";
import axios from "axios";

function TableMateriaux() {
  const [materiel, setMateriel] = useState([]);

  useEffect(() => {
    const fetchMateriaux = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/v1/getAllMateriaux");
        setMateriel(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMateriaux();
  }, []);

  const handleEditClick = async (id) => {
    try {
      // Fetch the specific material details
      const response = await axios.get(`http://localhost:8080/api/v1/getMaterielById/${id}`);
      const materialDetails = response.data;

      // Perform the edit logic here
      // For example, you can show a modal with pre-filled form fields
      console.log("Edit Material:", materialDetails);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteClick = async (id) => {
    try {
      // Perform the delete logic here
      // For example, send a DELETE request to the corresponding API endpoint
      await axios.delete(`http://localhost:8080/api/v1/deleteMaterielById/${id}`);

      // Remove the material from the local state
      const updatedMateriel = materiel.filter((material) => material.id !== id);
      setMateriel(updatedMateriel);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddClick = async () => {
    try {
      // Perform the add logic here
      // For example, show a modal with a form to add a new material
      console.log("Add Material");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div className="flex justify-center m-10 text-3xl text-base-300 font-bold justify-between">
        <h1>Table Materiaux</h1>
        <button className="btn btn-ms" onClick={handleAddClick}>
          Add Materiel
        </button>
      </div>
      <div className="scrolling-touch m-10 border border-base-300 ">
        <div className="overflow-x-auto">
          <table className="table">
            {/* head */}
            <thead>
              <tr className="text-base-300 text-lg">
                <th></th>
                <th>name</th>
                <th>quantite</th>
                <th>edit</th>
              </tr>
            </thead>
            <tbody>
              {/* rows */}
              {materiel.map((material) => (
                <tr key={material.id}>
                  <th></th>
                  <td>
                    <div>
                      <div className="font-bold">{material.name}</div>
                    </div>
                  </td>
                  <td>{material.quantite}</td>
                  <td className="flex gap-2">
                    <button
                      className="btn btn-ms"
                      onClick={() => handleEditClick(material.id)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-ms btn-ghost btn-outline"
                      onClick={() => handleDeleteClick(material.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TableMateriaux;
