import React, { useEffect, useState } from "react";
import axios from "axios";

function TableMateriaux() {
  const [materiel, setMateriel] = useState([]);

  useEffect(() => {
    const fetchMateriaux = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/v1/getAllMateriaux");
        setMateriel(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMateriaux();
  }, []);

  return (
    <div>
      <div className="flex justify-center m-10 text-3xl text-base-300 font-bold justify-between">
        <h1>Table Materiaux</h1>
        <button className="btn btn-ms">Add Materiel</button>
        <dialog id="my_modal_1" className="modal">
          <form
            method="dialog"
            className="modal-box flex flex-col items-center gap-10"
            // onSubmit={handleAddSubmit}
          >
            <h3 className="font-bold text-lg">Add Fournisseur</h3>
            <input
              type="text"
              placeholder="Name"
              className="input input-bordered w-full max-w-xs"
              // value={currentName}
              // onChange={(e) => setCurrentName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Contact"
              className="input input-bordered w-full max-w-xs"
              // value={currentContact}
              // onChange={(e) => setCurrentContact(e.target.value)}
            />
            <div className="modal-action flex">
              <button type="submit" className="btn">
                Add
              </button>
            </div>
          </form>
        </dialog>
      </div>
      <div className="scrolling-touch m-10 border border-base-300 ">
        <div className="overflow-x-auto">
          <table className="table">
            {/* head */}
            <thead>
              <tr className="text-base-300 text-lg">
                <th></th>
                <th>name</th>
                <th>quantite</th>
                <th>edit</th>
              </tr>
            </thead>
            <tbody>
              {/* rows */}
              {materiel.map((materiel) => (
                <tr key={materiel.id}>
                  <th></th>
                  <td>
                    <div>
                      <div className="font-bold">{materiel.name}</div>
                    </div>
                  </td>
                  <td>{materiel.quantite}</td>
                  <td className="flex gap-2">
                    <button className="btn btn-ms">Edit</button>
                    <button className="btn btn-ms btn-ghost btn-outline">
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TableMateriaux;
